import React, {useEffect} from 'react';
import {Link, useLocation, useParams, useHistory} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {Row, Col, ListGroup, Image, Form, Button, Card} from 'react-bootstrap'
import Message from '../components/Message'
import {addToCart} from '../actions/cartAction';


const CartScreen = ({location, history}) => {
  const { id: productId } = useParams()
  const qty = new URLSearchParams(useLocation().search).get('qty')
  const dispatch = useDispatch()

  const cart = useSelector(state => state.cart)
  const {cartItems} = cart

  console.log(cartItems)

  useEffect(() => {
    if(productId){
      dispatch(addToCart(productId, qty))
    }
  },[dispatch, productId, qty])

  const removeFromCartHandler = (id) => {
    console.log("remove")
  }

  return (
    <Row>
      <Col md={8}>
        <h1>עגלת קניות</h1>
        {cartItems.length === 0 ? <Message>עגלה ריקה<Link to='/'> חזור אחורה</Link></Message>:(
          <ListGroup variant='flush'>
        {cartItems.map((item) => (
          <ListGroup.Item key={item.product}>
            <Row>
              <Col md={2}>
                <Image src={item.image} alt={item.name} fluid rounded/>
              </Col>
              <Col md={3}>
                <Link to={`/product/${item.product}`}>{item.name}</Link>
              </Col>
              <Col md={2}>
                ${item.price}
              </Col>
              <Col md={2}>
                <Form.Control className='form-select' as='select' value={item.qty} onChange={(e) => dispatch(addToCart(item.product, Number(e.target.value)))}>
                        {[...Array(item.countInStock).keys()].map((x) => (
                          <option key={x + 1} value={x + 1} >
                            {x + 1}
                          </option>
                        ))}
                </Form.Control>
              </Col>
              <Col md={2}>
                <Button type="button" variant='light' onClick={() => removeFromCartHandler(item.product)}>
                  <i className="fas fa-trash"></i>
                </Button>
              </Col>
            </Row>
          </ListGroup.Item>
        ))}
          </ListGroup>
        )}
      </Col>
      <Col md={4}>
        <Card>
          <ListGroup variant='flush'>
            <ListGroup.Item>
              <h2>Subtotal ({cartItems.reduce((acc, item) => acc+item.qty, 0)}) items</h2>
            </ListGroup.Item>
          </ListGroup>
        </Card>
      </Col>
      
      
    </Row>
  )
}

export default CartScreen
